class MapLayer:
    def __init__(self):
        self.imagebg3 = load_image('res/background13.png')
        self.imagebg2 = load_image('res/background6.png')
        self.imagebg = load_image('res/background5.png')
        #self.imagebg2 = load_image('background2.png')
        self.mapState = 1
        self.x = 512
        self.y = 300
        self.x2 = 512
        self.y2 = 265
        self.imagebg4 =None
        self.imageState =  [[0]*13 for i in range(23)]
        self.sum = 0
        for i in range(23):
            for j in range(13):
                self.sum+=1
                if self.sum % 2 == 0:
                    self.imageState[i][j] = True
                elif self.sum % 2 == 1:
                    self.imageState[i][j] = False

    def changeMap(self, state):
        self.mapState = state
        if state == 1:
            self.imagebg3 = load_image('res/background13.png')
            self.imagebg2 = load_image('res/background6.png')
            self.imagebg = load_image('res/background5.png')
            self.x = 512
            self.y = 300
            self.x2 = 512
            self.y2 = 265
        if state == 2:
            self.imagebg3 = load_image('res/background13.png')
            self.imagebg2 = load_image('res/background11.png')
            self.imagebg = load_image('res/background10.png')
            self.x = 512
            self.y = 270
            self.x2 = 512
            self.y2 = 265
        if state == 3:
            self.imagebg3 = load_image('res/background13.png')
            self.imagebg2 = load_image('res/background11.png')
            self.imagebg = load_image('res/background55.png')
            self.imagebg4 = load_image('res/background56.png')
            self.x = 80
            self.y = 144
            self.x2 = 80
            self.y2 = 144
        if state == 4:
            self.imagebg3 = load_image('res/background13.png')
            self.imagebg2 = load_image('res/background15.png')
            self.imagebg = load_image('res/background16.png')
            self.x = 512
            self.y = 218
            self.x2 = 512
            self.y2 = 265

    def update(self):
        pass


    def draw(self):
        self.imagebg.opacify(1)
        if self.mapState == 1:
            self.imagebg3.draw(400,300)
            self.imagebg.draw(self.x, self.y)
            self.imagebg2.draw(self.x2,self.y2)
            self.imagebg2.draw(self.x2+1024,self.y2)
        elif self.mapState == 2:
            self.imagebg3.draw(400,300)
            self.imagebg.draw(self.x, self.y)
            self.imagebg.draw(self.x, self.y+300)
            self.imagebg2.draw(self.x2,self.y2)
            self.imagebg2.draw(self.x2+1024,self.y2)
        elif self.mapState == 3:
            #self.imagebg3.draw(400,300)
            for i in range(23):
                for j in range(13):
                    if self.imageState[i][j]:
                        self.imagebg4.draw(self.x + i*80, self.y+400  - j*144)
                    else:
                        self.imagebg.draw(self.x + i*80, self.y+400  - j*144)

        elif self.mapState == 4:
            self.imagebg3.draw(400,300)
            self.imagebg.draw(self.x, self.y)

            #self.imagebg2.draw(self.x2,self.y2)
            #self.imagebg2.draw(self.x2+1024,self.y2)
        pass

class Map:

    def __init__(self):
        global  font
        self.image = load_image('res/background4.png')
        self.image_dark = load_image('res/background_dark.png')
        self.image2 = load_image('res/interface2.png')
        self.mapState = 1
        self.x = 450*2
        self.y = 350
        self.x2 = 600
        self.y2 = 450
        self.opacify = 0.2
        self.herohp = 0
        self.firstTime = get_time()
        self.maxtime = 570
        self.currentTime = 0
        self.night = False
        self.change = False
        self.viewstate = 0
        font = load_font('ENCR10B.TTF')

    def changeMap(self, viewstate ,state, mapX, mapY):
        self.viewstate = viewstate
        self.mapState = state
        self.x = mapX
        self.y = mapY

    def update(self):
        self.currentTime = (get_time()-self.firstTime)+1

        #print(self.maxtime - self.currentTime , self.currentTime)
        if self.maxtime - self.currentTime < 0.0:
            if self.night:
                self.night = False
            else:
                self.night = True
            self.firstTime = get_time()
            self.maxtime = 570
            self.change = True
        if self.night == True:
            if self.opacify < 0.6:
                self.opacify += 0.01
        elif self.night == False:
            if self.opacify >= 0:
                self.opacify -= 0.01

    def get_hero_hp(self,hp):
        self.herohp = hp

    def draw(self):

        self.image.opacify(self.opacify)
        if self.viewstate == 0:
            self.image.draw(400,300)
        elif self.viewstate == 1:
            self.image_dark.draw(self.x2,self.y2)
            pass
        #self.image2.opacify(0.1)
        #self.image2.draw(750,530)
        #font.draw(650, 575,'H  P:%3.0f' % self.herohp)
        font.draw(670, 550,'H  P:%3.0f' % self.herohp)
        font.draw(670, 525,'Time:%3.0f' % (self.maxtime - self.currentTime) )
        if self.night == True:
            font.draw(670, 500,'  Moon')
        else:
            font.draw(670, 500,'   Sun')

